import { useState, useRef, useEffect } from 'react';
import './App.css';

function App() {
    const [promptArea, setPromptArea] = useState('');
    const [chatHistory, setChatHistory] = useState([
        { sender: 'bot', message: 'Hi there ðŸ‘‹<br />We typically reply within a few minutes.' },
    ]);
    const [isLoading, setIsLoading] = useState(false);
    const messagesEndRef = useRef(null);

    useEffect(() => {
        if (messagesEndRef.current) {
            messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [chatHistory]);

    // ... (rest of your component logic remains the same)

    return (
        <div className="chat-container">
            <div className="chat-card">
                <div className="chat-header">
                    <h2>Chat with Our AI</h2>
                </div>
                <div className="chat-box">
                    <div className="messages">
                        {chatHistory.map((msg, index) => (
                            <div
                                key={index}
                                className={`message ${msg.sender}`}
                                dangerouslySetInnerHTML={{ __html: msg.message }}
                            >
                            </div>
                        ))}
                        <div ref={messagesEndRef} />
                    </div>
                </div>
                <div className="chat-input">
                    <textarea
                        className="input-textarea"
                        rows={2}
                        placeholder="Type your message..."
                        onChange={(e) => setPromptArea(e.target.value)}
                        value={promptArea}
                        onKeyDown={handleKeyPress}
                    />
                    <button className="send-button" onClick={handleSubmit} disabled={isLoading}>
                        {isLoading ? (
                            <span className="loading-dots">
                                <span>.</span><span>.</span><span>.</span>
                            </span>
                        ) : 'Send'}
                    </button>
                </div>
            </div>
        </div>
    );
}

export default App;
