        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault(); // Prevent default behavior of Enter key (adding new line)
            handleSubmit();
        }
    };

    // Function to simulate translation to Arabic (Replace this with an actual API)
    const translateToArabic = async (text) => {
        // Simulating translation (you can replace this logic with an API like Google Translate)
        let translatedText = text;

        // Perform basic clean-up to ensure no unwanted characters (e.g., Chinese or English) remain
        // Here, we are ensuring only Arabic text is retained
        translatedText = translatedText.replace(/[^Ø§-ÙŠ\s]/g, ''); // Remove non-Arabic characters

