import { useState } from 'react';
import './App.css';

function App() {
    const [promptArea, setPromptArea] = useState('');
    const [chatHistory, setChatHistory] = useState([
        { sender: 'bot', message: 'Hi there ðŸ‘‹\nWe typically reply within a few minutes.' },
    ]);
    const [isLoading, setIsLoading] = useState(false);

    const handleSubmit = async () => {
        const userMessage = promptArea.trim();
        if (!userMessage) return;

        // Add user's message to chat history
        setChatHistory((prev) => [
            ...prev,
            { sender: 'user', message: userMessage },
        ]);
        setPromptArea(''); // Clear the input after submitting
        setIsLoading(true); // Start loading

        const url = 'http://localhost:3050/api/prompt';
        let tmpPromptResponse = '';
        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ prompt: userMessage }),
            });

            if (!response.body) throw new Error('No response body');

            const reader = response.body.getReader();
            const decoder = new TextDecoder();
            let accumulatedText = '';
            let lineCount = 0;

            // Simulate letter-by-letter response
            while (true) {
                const { value, done } = await reader.read();
                if (done) break;

                accumulatedText += decoder.decode(value, { stream: true });

                // Limit to 5-6 lines
                const lines = accumulatedText.split('\n');
                if (lines.length >= 6) {
                    accumulatedText = lines.slice(0, 6).join('\n');
                    break;
                }

                setTimeout(() => {
                    setChatHistory((prev) => [
                        ...prev,
                        { sender: 'bot', message: accumulatedText },
                    ]);
                }, 100); // Typing effect delay

            }
        } catch (error) {
            console.error('Error:', error);
            setChatHistory((prev) => [
                ...prev,
                { sender: 'bot', message: 'Error communicating with the server.' },
            ]);
        } finally {
            setIsLoading(false); // End loading
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-card">
                <div className="chat-header">
                    <h2>Chat with Our AI</h2>
                </div>
                <div className="chat-box">
                    <div className="messages">
                        {chatHistory.map((msg, index) => (
                            <div
                                key={index}
                                className={`message ${msg.sender}`}
                            >
                                <p>{msg.message}</p>
                            </div>
                        ))}
                    </div>
                </div>
                <div className="chat-input">
                    <textarea
                        className="input-textarea"
                        rows={2}
                        placeholder="Type your message..."
                        onChange={(e) => setPromptArea(e.target.value)}
                        value={promptArea}
                    />
                    <button className="send-button" onClick={handleSubmit} disabled={isLoading}>
                        {isLoading ? 'Sending...' : 'Send'}
                    </button>
                </div>
            </div>
        </div>
    );
}

export default App;
