import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

const botService = {
  name: "Lion Pro Dev Bot",
  welcomeMessage: "Welcome to Lion Pro Dev! How can we assist you today?",
  services: [
    "Custom Software Development",
    "Web Design & Development",
    "Mobile Application Development",
    "Cloud Solutions",
    "Digital Marketing"
  ],
  contactInfo: {
    phone: "+1 (123) 456-7890",
    email: "contact@lionprodev.com",
    website: "https://lionprodev.com"
  },
  team: [
    { name: "Pedro M. Matar", role: "Developer" },
    { name: "Philip Cutting", role: "CTO" },
    { name: "Alvin", role: "Developer" },
    { name: "Mark Hudson", role: "Lead Engineer" },
    { name: "Stephanie Greene", role: "Project Manager" }
  ],

  handleMessage: function (userMessage) {
    if (userMessage.includes("services")) {
      return `We offer the following services: ${this.services.join(", ")}`;
    } else if (userMessage.includes("contact")) {
      return `You can contact us at: \nEmail: ${this.contactInfo.email} \nPhone: ${this.contactInfo.phone}`;
    } else if (userMessage.includes("team")) {
      return "Meet our team: " + this.team.map(member => `${member.name}, ${member.role}`).join(", ");
    } else {
      return this.welcomeMessage;
    }
  }
};

function App() {
  const [userMessage, setUserMessage] = useState('');
  const [botResponse, setBotResponse] = useState('');
  const [messages, setMessages] = useState([]);

  const handleUserInput = (e) => {
    setUserMessage(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = botService.handleMessage(userMessage);
    setMessages([...messages, { user: userMessage, bot: response }]);
    setBotResponse(response);

    try {
      const apiResponse = await axios.post('https://groq.com/api/endpoint', {
        query: userMessage
      });
      if (apiResponse.data) {
        setBotResponse(apiResponse.data.response);
      }
    } catch (error) {
      console.error("Error: ", error);
      setBotResponse("Sorry, there was an error. Please try again.");
    }
    setUserMessage('');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>{botService.name}</h2>
        <div className="chat-box">
          {messages.map((msg, index) => (
            <div key={index}>
              <div><strong>User:</strong> {msg.user}</div>
              <div><strong>Bot:</strong> {msg.bot}</div>
            </div>
          ))}
          {/* <div><strong>Bot:</strong> {botResponse}</div> */}
        </div>
        <form onSubmit={handleSubmit}>
          <input 
            type="text" 
            value={userMessage} 
            onChange={handleUserInput} 
            placeholder="Ask me anything..." 
            required 
          />
          <button type="submit">Send</button>
        </form>
      </header>
    </div>
  );
}

export default App;
