import { useState } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([
    { sender: 'bot', text: 'Nice!', isTitle: false },
    { sender: 'bot', text: 'What is your role at Acme Corp?', isTitle: false },
  ]);
  const [inputValue, setInputValue] = useState('');
  const [options, setOptions] = useState(['Marketing', 'Sales', 'Support']);

  const handleOptionClick = (option) => {
    setMessages([...messages, 
      { sender: 'user', text: option, isTitle: false },
      { sender: 'bot', text: `Our tool is great for ${option.toLowerCase()} teams of all sizes! How many people are on yours?`, isTitle: false }
    ]);
    setOptions([]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!inputValue.trim()) return;

    setMessages([...messages, 
      { sender: 'user', text: inputValue, isTitle: false },
      { sender: 'bot', text: 'Thanks! A representative will contact you shortly.', isTitle: false }
    ]);
    setInputValue('');
  };

  return (
    <div className="chat-container">
      <div className="chat-window">
        <div className="chat-header">
          <div className="bot-title">
            <div className="status-indicator"></div>
            <h2>LeadBot</h2>
            <span className="status">Online Now</span>
          </div>
        </div>

        <div className="chat-messages">
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.sender}`}>
              {msg.sender === 'bot' && (
                <div className="bot-avatar">LB</div>
              )}
              <div className="message-content">
                {msg.sender === 'bot' && msg.isTitle && (
                  <div className="bot-title-message">LeadBot</div>
                )}
                <div className="message-text">{msg.text}</div>
              </div>
            </div>
          ))}

          {options.length > 0 && (
            <div className="options-container">
              {options.map((option, index) => (
                <button 
                  key={index} 
                  className="option-button"
                  onClick={() => handleOptionClick(option)}
                >
                  {option}
                </button>
              ))}
            </div>
          )}
        </div>

        <form onSubmit={handleSubmit} className="chat-input">
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder="Reply to LeadBot..."
            className="message-input"
          />
          <div className="branding">We're by Drift</div>
        </form>
      </div>
    </div>
  );
}

export default App;
