import { useState, useRef, useEffect } from 'react';
import './App.css';

function App() {
  const [promptArea, setPromptArea] = useState('');
  const [chatHistory, setChatHistory] = useState([
    {
      sender: 'bot',
      arabic: 'ŸÖÿ±ÿ≠ÿ®Ÿãÿß üëã\nŸÜÿ±ÿØ ÿπÿßÿØÿ©Ÿã ÿÆŸÑÿßŸÑ ÿØŸÇÿßÿ¶ŸÇ.'.replace(/\n/g, '<br />'),
      english: 'Hi there üëã\nWe typically reply within a few minutes.'.replace(/\n/g, '<br />'),
      isArabic: true,
    },
  ]);
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [chatHistory]);

  const handleSubmit = async () => {
    const userMessage = promptArea.trim();
    if (!userMessage) return;

    setChatHistory((prev) => [
      ...prev,
      { sender: 'user', message: userMessage },
    ]);
    setPromptArea('');
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:3050/api/prompt', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt: userMessage }),
      });

      const data = await response.json();

      if (data.error) throw new Error(data.error);

      const isPromptArabic = data.prompt_language === 'ar';

      setChatHistory((prev) => [
        ...prev,
        {
          sender: 'bot',
          arabic: data.arabic.replace(/\n/g, '<br />'),
          english: data.english.replace(/\n/g, '<br />'),
          isArabic: isPromptArabic,
        },
      ]);
    } catch (error) {
      console.error('Error:', error);
      setChatHistory((prev) => [
        ...prev,
        {
          sender: 'bot',
          arabic: 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ.'.replace(/\n/g, '<br />'),
          english: 'Error communicating with the server.'.replace(/\n/g, '<br />'),
          isArabic: true,
        },
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit();
    }
  };

  // THIS is the fixed toggleLanguage handler that properly updates state immutably
  const toggleLanguage = (index) => {
    setChatHistory((prev) =>
      prev.map((msg, i) =>
        i === index && msg.sender === 'bot' ? { ...msg, isArabic: !msg.isArabic } : msg
      )
    );
  };

  return (
    <div className="chat-container">
      <div className="chat-card">
        <div className="chat-header">
          <h2>Chat with Our AI</h2>
        </div>
        <div className="chat-box">
          <div className="messages">
            {chatHistory.map((msg, index) => (
              <div key={index} className={`message ${msg.sender}`}>
                {msg.sender === 'bot' ? (
                  <>
                    <div
                      dangerouslySetInnerHTML={{
                        __html: msg.isArabic ? msg.arabic : msg.english,
                      }}
                    />
                    <button
                      className="translate-button"
                      onClick={() => toggleLanguage(index)}
                    >
                      {msg.isArabic ? 'Translate to English' : 'ÿ™ÿ±ÿ¨ŸÖÿ© ÿ•ŸÑŸâ ÿßŸÑÿπÿ±ÿ®Ÿäÿ©'}
                    </button>
                  </>
                ) : (
                  <div>{msg.message}</div>
                )}
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
        </div>
        <div className="chat-input">
          <textarea
            className="input-textarea"
            rows={2}
            placeholder="Type your message..."
            onChange={(e) => setPromptArea(e.target.value)}
            value={promptArea}
            onKeyDown={handleKeyPress}
          />
          <button
            className="send-button"
            onClick={handleSubmit}
            disabled={isLoading}
          >
            {isLoading ? 'Sending...' : 'Send'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
