import { useState, useRef, useEffect } from 'react';
import './App.css';

function App() {
    const [promptArea, setPromptArea] = useState('');
    const [chatHistory, setChatHistory] = useState([
        { sender: 'bot', message: 'Hi there ðŸ‘‹\nWe typically reply within a few minutes.' },
    ]);
    const [isLoading, setIsLoading] = useState(false);
    const messagesEndRef = useRef(null); // Reference for auto-scrolling

    // Auto-scroll to the bottom whenever the chatHistory changes
    useEffect(() => {
        if (messagesEndRef.current) {
            messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [chatHistory]);
const handleSubmit = async () => {
  const userMessage = promptArea.trim();
  if (!userMessage) return;

  // Add user message to chat
  setChatHistory((prev) => [...prev, { sender: 'user', message: userMessage }]);
  setPromptArea('');
  setIsLoading(true);

  try {
    const response = await fetch('http://localhost:3050/api/prompt', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: userMessage }),
    });

    if (!response.body) throw new Error('No response body');

    const reader = response.body.getReader();
    const decoder = new TextDecoder();

    // Add empty bot message placeholder for streaming update
    setChatHistory((prev) => [...prev, { sender: 'bot', message: '' }]);

    while (true) {
      const { value, done } = await reader.read();
      if (done) break;

      const chunk = decoder.decode(value, { stream: true });

      // Optional cleanup: remove asterisks and convert newlines to <br>
      const cleanedChunk = chunk.replace(/\*/g, '').replace(/\n/g, '<br />');

      // Update bot message incrementally
      setChatHistory((prev) => {
        const newHistory = [...prev];
        // Find last bot message (the empty one we just pushed)
        for (let i = newHistory.length - 1; i >= 0; i--) {
          if (newHistory[i].sender === 'bot') {
            newHistory[i] = {
              ...newHistory[i],
              message: newHistory[i].message + cleanedChunk,
            };
            break;
          }
        }
        return newHistory;
      });
    }
  } catch (error) {
    console.error('Error:', error);
    setChatHistory((prev) => [
      ...prev,
      { sender: 'bot', message: 'Error communicating with the server.' },
    ]);
  } finally {
    setIsLoading(false);
  }
};


    const handleKeyPress = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault(); // Prevent default behavior of Enter key (adding new line)
            handleSubmit();
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-card">
                <div className="chat-header">
                    <h2>Chat with Our AI</h2>
                </div>
                <div className="chat-box">
                    <div className="messages">
                        {chatHistory.map((msg, index) => (
                            <div
                                key={index}
                                className={`message ${msg.sender}`}
                                dangerouslySetInnerHTML={{ __html: msg.message }} // Render message with HTML (line breaks)
                            >
                            </div>
                        ))}
                        <div ref={messagesEndRef} /> {/* Scroll reference */}
                    </div>
                </div>
                <div className="chat-input">
                    <textarea
                        className="input-textarea"
                        rows={2}
                        placeholder="Type your message..."
                        onChange={(e) => setPromptArea(e.target.value)}
                        value={promptArea}
                        onKeyDown={handleKeyPress} // Listen for Enter key
                    />
                    <button className="send-button" onClick={handleSubmit} disabled={isLoading}>
                        {isLoading ? 'Sending...' : 'Send'}
                    </button>
                </div>
            </div>
        </div>
    );
}

export default App;
