import { useState, useRef, useEffect } from 'react';
import './App.css';

function App() {
    const [promptArea, setPromptArea] = useState('');
    const [chatHistory, setChatHistory] = useState([
        { sender: 'bot', message: 'مرحبًا! 👋\nعادةً ما نرد في غضون بضع دقائق.' }, // Initial greeting in Arabic
    ]);
    const [isLoading, setIsLoading] = useState(false);
    const messagesEndRef = useRef(null); // Reference for auto-scrolling

    // Auto-scroll to the bottom whenever the chatHistory changes
    useEffect(() => {
        if (messagesEndRef.current) {
            messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [chatHistory]);

    const handleSubmit = async () => {
        const userMessage = promptArea.trim();
        if (!userMessage) return;

        // Add user's message to chat history
        setChatHistory((prev) => [
            ...prev,
            { sender: 'user', message: userMessage },
        ]);
        setPromptArea(''); // Clear the input after submitting
        setIsLoading(true); // Start loading

        const url = 'http://localhost:3050/api/prompt';
        let tmpPromptResponse = '';
        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ prompt: userMessage }),
            });

            if (!response.body) throw new Error('No response body');

            const reader = response.body.getReader();
            const decoder = new TextDecoder();

            while (true) {
                const { value, done } = await reader.read();
                if (done) break;

                tmpPromptResponse += decoder.decode(value, { stream: true });
            }

            // Fix: Adding line breaks and sanitizing for asterisks or any unwanted characters
            tmpPromptResponse = tmpPromptResponse.replace(/\*/g, '');  // Remove asterisks (if any)
            tmpPromptResponse = tmpPromptResponse.replace(/\n/g, '<br />');  // Adding line breaks

            // Translate the response to Arabic before showing it
            const translatedResponse = await translateToArabic(tmpPromptResponse);

            // Once the full response is received and translated, update the state
            setChatHistory((prev) => [
                ...prev,
                { sender: 'bot', message: translatedResponse },
            ]);
        } catch (error) {
            console.error('Error:', error);
            setChatHistory((prev) => [
                ...prev,
                { sender: 'bot', message: 'حدث خطأ في التواصل مع الخادم.' }, // Error message in Arabic
            ]);
        } finally {
            setIsLoading(false); // End loading
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault(); // Prevent default behavior of Enter key (adding new line)
            handleSubmit();
        }
    };

    // Function to translate the text to Arabic
    const translateToArabic = async (text) => {
        // You can implement an API call to a translation service here
        // For simplicity, you can use Google Translate API or any other service.
        // Below is a mockup for translation.

        const translatedText = text; // Replace this line with the actual translation call
        return translatedText; // Return translated text
    };

    return (
        <div className="chat-container" dir="rtl"> {/* Set direction to right-to-left for Arabic */}
            <div className="chat-card">
                <div className="chat-header">
                    <h2>الدردشة مع الذكاء الاصطناعي الخاص بنا</h2> {/* Header in Arabic */}
                </div>
                <div className="chat-box">
                    <div className="messages">
                        {chatHistory.map((msg, index) => (
                            <div
                                key={index}
                                className={`message ${msg.sender}`}
                                dangerouslySetInnerHTML={{ __html: msg.message }} // Render message with HTML (line breaks)
                            >
                            </div>
                        ))}
                        <div ref={messagesEndRef} /> {/* Scroll reference */}
                    </div>
                </div>
                <div className="chat-input">
                    <textarea
                        className="input-textarea"
                        rows={2}
                        placeholder="اكتب رسالتك..."
                        onChange={(e) => setPromptArea(e.target.value)}
                        value={promptArea}
                        onKeyDown={handleKeyPress} // Listen for Enter key
                    />
                    <button className="send-button" onClick={handleSubmit} disabled={isLoading}>
                        {isLoading ? 'جاري الإرسال...' : 'إرسال'}
                    </button>
                </div>
            </div>
        </div>
    );
}

export default App;
